import React, { useState, useEffect } from 'react';
import './PublicForum.css';

function PublicForum() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [user, setUser] = useState(null); // This would come from your auth system

  // Simulate message cleanup after 48 hours
  useEffect(() => {
    const cleanup = setInterval(() => {
      const twoDaysAgo = new Date(Date.now() - 48 * 60 * 60 * 1000);
      setMessages(prevMessages => 
        prevMessages.filter(msg => new Date(msg.timestamp) > twoDaysAgo)
      );
    }, 1000 * 60 * 60); // Check every hour

    return () => clearInterval(cleanup);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!user) {
      alert('Please register or log in to post messages');
      return;
    }
    if (newMessage.trim()) {
      const message = {
        id: Date.now(),
        text: newMessage,
        user: user.name,
        timestamp: new Date().toISOString(),
      };
      setMessages(prev => [...prev, message]);
      setNewMessage('');
    }
  };

  return (
    <section id="forum" className="forum-section">
      <h2>Community Forum</h2>
      <div className="forum-container">
        <div className="messages-container">
          {messages.map(message => (
            <div key={message.id} className="message">
              <div className="message-header">
                <span className="user-name">{message.user}</span>
                <span className="timestamp">
                  {new Date(message.timestamp).toLocaleString()}
                </span>
              </div>
              <p className="message-text">{message.text}</p>
            </div>
          ))}
        </div>
        <form onSubmit={handleSubmit} className="message-form">
          <textarea
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder={user ? "Write your message..." : "Please register to post messages"}
            disabled={!user}
          />
          <button type="submit" disabled={!user}>
            Send Message
          </button>
        </form>
      </div>
    </section>
  );
}

export default PublicForum;
