import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './RegisterForm.css';

function RegisterForm({ setUser }) {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    role: '',
    firstName: '',
    lastName: '',
    grade: '',
    birthday: '',
    email: '',
    phone: '',
    profilePicture: null
  });

  const [isCoach, setIsCoach] = useState(false);
  const [existingUsers, setExistingUsers] = useState([]);

  // Load existing users from localStorage on component mount
  useEffect(() => {
    const users = JSON.parse(localStorage.getItem('users') || '[]');
    setExistingUsers(users);
  }, []);

  const checkForDuplicateUser = (firstName, lastName) => {
    return existingUsers.some(user => 
      user.firstName.toLowerCase() === firstName.toLowerCase() && 
      user.lastName.toLowerCase() === lastName.toLowerCase()
    );
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setFormData(prev => ({
      ...prev,
      profilePicture: file
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Check for duplicate profile
    if (checkForDuplicateUser(formData.firstName, formData.lastName)) {
      alert('A profile with this name already exists. Please use a different name or contact support if this is an error.');
      return;
    }

    // Create user object from form data
    const newUser = {
      ...formData,
      isCoach,
      id: Date.now() // Add unique identifier
    };

    // Update users array in localStorage
    const updatedUsers = [...existingUsers, newUser];
    localStorage.setItem('users', JSON.stringify(updatedUsers));

    // Set current user
    localStorage.setItem('currentUser', JSON.stringify(newUser));
    setUser(newUser);

    // Redirect to homepage
    navigate('/');
  };

  const handleReturn = () => {
    navigate('/');
  };

  const handleCoachCheck = (e) => {
    setIsCoach(e.target.checked);
  };

  return (
    <div className="register-form-container">
      <form onSubmit={handleSubmit} className="register-form">
        <h2>Registration</h2>

        <div className="form-group">
          <label htmlFor="role">I am a:</label>
          <select
            id="role"
            name="role"
            value={formData.role}
            onChange={handleInputChange}
            required
          >
            <option value="">Select Role</option>
            <option value="coach">Coach</option>
            <option value="player">Player</option>
            <option value="parent">Parent</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            value={formData.firstName}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="lastName">Last Name</label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            value={formData.lastName}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label className="checkbox-label">
            <input
              type="checkbox"
              checked={isCoach}
              onChange={handleCoachCheck}
            />
            I am a coach
          </label>
        </div>

        {!isCoach && (
          <div className="form-group">
            <label htmlFor="grade">Grade</label>
            <select
              id="grade"
              name="grade"
              value={formData.grade}
              onChange={handleInputChange}
              required={!isCoach}
            >
              <option value="">Select Grade</option>
              <option value="5">5th Grade</option>
              <option value="6">6th Grade</option>
              <option value="7">7th Grade</option>
              <option value="8">8th Grade</option>
              <option value="9">9th Grade</option>
              <option value="10">10th Grade</option>
              <option value="11">11th Grade</option>
              <option value="12">12th Grade</option>
            </select>
          </div>
        )}

        <div className="form-group">
          <label htmlFor="birthday">Birthday</label>
          <input
            type="date"
            id="birthday"
            name="birthday"
            value={formData.birthday}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone Number</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleInputChange}
            pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
            placeholder="123-456-7890"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="profilePicture">Profile Picture</label>
          <input
            type="file"
            id="profilePicture"
            name="profilePicture"
            accept="image/*"
            onChange={handleImageUpload}
          />
        </div>

        <div className="button-container">
          <button type="button" className="return-button" onClick={handleReturn}>
            Return to Homepage
          </button>
          <button type="submit" className="submit-button">Register</button>
        </div>
      </form>
    </div>
  );
}

export default RegisterForm;
