import React, { useState, useEffect } from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import './App.css';
import Logo from './components/Logo';
import Profile from './components/Profile';
import RegisterForm from './components/RegisterForm';
import PublicForum from './components/PublicForum';

function App() {
  const [user, setUser] = useState(null);
  const venmoLink = "#"; // Add your Venmo link here

  // Check for logged in user on app load
  useEffect(() => {
    const currentUser = localStorage.getItem('currentUser');
    if (currentUser) {
      setUser(JSON.parse(currentUser));
    }
  }, []);

  return (
    <div className="app">
      <nav className="navbar">
        <div className="nav-content">
          <Logo />
          <ul className="nav-links">
            <li><a href="#home">Home</a></li>
            <li><a href="#coaches">Coaches</a></li>
            {!user && (
              <li><Link to="/register" className="register-button">Register Here</Link></li>
            )}
            <li><a href={venmoLink} target="_blank" rel="noopener noreferrer" className="payment-link">Pay Now</a></li>
            {user && (
              <li className="profile-link">
                <Link to="/profile" className="profile-icon">
                  {user.profilePicture ? (
                    <img 
                      src={user.profilePicture} 
                      alt={`${user.firstName}'s profile`} 
                      className="profile-image"
                    />
                  ) : (
                    <div className="profile-initial">
                      {user.firstName.charAt(0)}
                    </div>
                  )}
                </Link>
              </li>
            )}
          </ul>
        </div>
      </nav>

      <Routes>
        <Route path="/register" element={<RegisterForm setUser={setUser} />} />
        <Route path="/profile" element={<Profile user={user} />} />
      </Routes>

      <section className="hero">
        {/* Hero content */}
      </section>

      <section id="coaches" className="coaches-section">
        {/* Coaches content */}
      </section>

      <PublicForum user={user} />
    </div>
  );
}

export default App;
